 html {
  height: 100%;
  width: 100%;
}

body {
  margin: 0;               /* removes the 8px default margin */
  padding: 0;
  width: 100%;
  overflow-x: hidden;      /* prevents stray horizontal scroll/gutters */
  background: #ffffff;
  position: relative;
 /* page background color */
}
img, svg {
  display: block;
  max-width: 100%;
  height: auto;
}
/* ---------- layout base ---------- */
:root{
  --container: 1200px;
  --accent: #ff6a4a;     /* CTA orange */
  --ink: #0f1115;
  --on-dark: #ffffff;
  --ink-muted: #d6d9de;
}

.container{
  max-width: var(--container);
  width: 100%;
  margin-inline: auto;
  padding-inline: 20px;
  box-sizing: border-box;
}

/* =========== NAVBAR =========== */
.navbar {
  position: sticky;
  top: 0;
  z-index: 100;
  background: #fff;
  border-bottom: 1px solid #eee;
}

.nav__inner {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 1rem 2rem;
  max-width: 1200px;
  margin: 0 auto;
}

.logo {
  display: flex;
  align-items: center;
  gap: 8px;
  font-weight: 700;
  font-size: 1.1rem;
  color: #0f172a;
  text-decoration: none;
}

.logo img {
  width: 28px;
  height: 28px;
}

.nav__list {
  display: flex;
  gap: 2rem;
  list-style: none;
  margin: 0;
  padding: 0;
}

.nav__list a {
  text-decoration: none;
  color: #0f172a;
  font-weight: 500;
  transition: color 0.2s;
}

.nav__list a:hover {
  color: #475569;
}

/* CTA button */
.btn--cta {
  background: #ff6a4a;
  color: #fff;
  padding: 0.6rem 1.2rem;
  border-radius: 6px;
  text-decoration: none;
  font-weight: 600;
  transition: background 0.3s;
}

.btn--cta:hover {
  background: #ff5430;
}

/* Mobile toggle */
.nav-toggle {
  display: none;
  flex-direction: column;
  gap: 5px;
  background: none;
  border: none;
  cursor: pointer;
}

.nav-toggle .bar {
  width: 22px;
  height: 2px;
  background: #0f172a;
}
/* ---------- HERO ---------- */
.hero{
  position: relative;
  color: var(--on-dark);
  /* keep space under sticky header (adjust if your header is taller) */
  padding-block: clamp(64px, 7vw, 120px);
  min-height: clamp(560px, 78vh, 840px);
  overflow: clip;
}

/* Background layer: image + dark overlays + bottom fade */
.hero__bg{
  position: absolute;
  top: 0; bottom: 0;
  left: 50%;
  width: 100vw;                 /* force full viewport width */
  transform: translateX(-50%);  /* center it */
  background:
    radial-gradient(1200px 600px at 22% 12%, rgba(255,255,255,.06), transparent 60%),
    linear-gradient(180deg, rgba(6,10,18,.88) 0%, rgba(6,10,18,.8) 42%, rgba(6,10,18,.55) 70%, rgba(6,10,18,.25) 100%),
    url("assets/img/hero-bg.jpg") center/cover no-repeat;
  pointer-events: none;
  z-index: 0;
}
.hero__bg{
  position: absolute;
  inset: 0;                 /* top:0; right:0; bottom:0; left:0 */
  width: auto;              /* avoid 100vw */
  transform: none;          /* no centering shift needed */
  background:
    radial-gradient(1200px 600px at 22% 12%, rgba(255,255,255,.06), transparent 60%),
    linear-gradient(180deg, rgba(6,10,18,.88) 0%, rgba(6,10,18,.8) 42%, rgba(6,10,18,.55) 70%, rgba(6,10,18,.25) 100%),
    url("assets/img/hero-bg.jpg") center/cover no-repeat;
  pointer-events: none;
  z-index: 0;
}

.hero::before{
  content: "";
  position: absolute;
  left: 0; right: 0; bottom: 0;
  width: auto;              /* no 120vw */
  height: 180px;
  background: linear-gradient(
    to bottom,
    rgba(255,255,255,0) 0%,
    rgba(255,255,255,0.15) 35%,
    rgba(255,255,255,0.6) 70%,
    rgba(255,255,255,1) 100%
  );
  filter: blur(14px);
  opacity: 0.9;
  pointer-events: none;
  z-index: 1;
}
@media (min-width: 1024px) {
  .hero::before { height: 220px; filter: blur(18px); opacity: 1; }
}

/* keep content above the glow and bg */
.hero__grid { position: relative; z-index: 2; }


/* 3) iOS safe-area (notch) friendliness so the edges look intentional */
@supports (padding: max(0px)) {
  .navbar, .hero .container {
    padding-left: max(20px, env(safe-area-inset-left));
    padding-right: max(20px, env(safe-area-inset-right));
  }
}
.hero::after{
  content:""; position:absolute; left:0; right:0; bottom:-1px; height:140px;
  background: linear-gradient(to bottom, rgba(6,10,18,0), rgba(6,10,18,1));
  pointer-events:none;
}

/* Two-column grid with tight alignment */
.hero__grid{
  position: relative; z-index: 1;
  display: grid;
  grid-template-columns: minmax(0, 560px) minmax(0, 520px);
  align-items: center;
  gap: clamp(24px, 4vw, 56px);
}

/* Typography tuned to your screenshot */
.hero__title{
  margin: 0 0 14px;
  font-weight: 700;
  letter-spacing: .2px;
  line-height: 1.06;
  font-size: clamp(2.25rem, 1.1rem + 4.8vw, 3.75rem);
  text-shadow: 0 2px 22px rgba(0,0,0,.28);
}

.hero__subtitle{
  margin: 0 0 26px;
  color: var(--ink-muted);
  font-size: clamp(1rem, .9rem + .5vw, 1.125rem);
  max-width: 56ch;
}

/* Actions */
.btn{
  display:inline-flex; align-items:center; gap:8px;
  padding: 12px 18px; border-radius: 10px; font-weight: 600;
  text-decoration: none; border: 1px solid transparent;
}
.btn .icon{ width: 18px; height: 18px; }
.btn--primary{ background: var(--accent); color:#fff; }
.btn--primary:hover{ filter: brightness(.95); }
.btn--ghost{
  background: rgba(255,255,255,.04);
  color:#fff; border-color: rgba(255,255,255,.35); border-width:1px; border-style:solid;
  backdrop-filter: blur(4px);
}
.btn--ghost:hover{ border-color: rgba(255,255,255,.7); }

.hero__actions{ display:flex; gap:14px; flex-wrap:wrap; }

/* Mockup card styling to match rounded device feel */
.hero__media{
  position: relative;
  display: grid; place-items: center;
}
.hero__media img{
  width: 100%; height: auto;
  border-radius: 22px;
  background: #fff;
  box-shadow:
    0 20px 60px rgba(0,0,0,.32),
    0 2px 0 6px rgba(255,255,255,.08) inset;
  outline: 10px solid rgba(255,255,255,.22);
  /* align visually with the left copy (slight lift) */
  transform: translateY(6px);
}

/* ---------- ABOUT INTRO ---------- */
.about-intro {
  position: relative;
  background: #fff;
  margin-top: -32px;
  padding-block: 60px 48px;
  color: #0f172a;
  z-index: 2;
}

/* 1️⃣ Container alignment - same padding as hero */
.about__grid {
  display: flex;
  align-items: center;
  gap: 1.8rem;                        /* tighter gap between map & text */
  max-width: 1200px;
  margin-inline: auto;
  padding-inline: 20px;               /* matches your .container’s 20px */
  justify-content: flex-start;        /* align to left */
}

/* 2️⃣ Map styling */
.about__image {
  width: 150px;
  height: auto;
  flex: 0 0 auto;
}

.about__image img {
  width: clamp(100px, 14vw, 150px);
  height: auto;
  opacity: 0.7;
  filter: grayscale(100%);
}

/* 3️⃣ Text styling */
.about__text {
  margin-left: clamp(-40px, -2vw, -20px);
  flex: 1;
  text-align: left;
}

.about__text h2 {
  margin: 0 0 6px;
  font-weight: 700;
  font-size: clamp(1.8rem, 1.1rem + 1.5vw, 2.3rem);
  color: #0f172a;
}

.about__text p {
  margin: 0;
  font-size: 1.1rem;
  color: #6b7280;
  line-height: 1.6;
}
/* ---------- ABOUT FEATURE (split background) ---------- */
.about-feature {
  position: relative;
  background: linear-gradient(
    to right,
    #FBF6E1 0%,
    #FBF6E1 52%,    /* beige covers left half */
    #ffffff 52%,    /* white begins here */
    #ffffff 100%
  );
  padding-block: clamp(80px, 12vw, 140px);
  color: #0f172a;
  overflow: hidden;
}

.about-feature__grid {
  display: grid;
  grid-template-columns: 1.1fr 1fr; /* image | text */
  align-items: center;
  gap: clamp(40px, 6vw, 80px);
  max-width: 1200px;
  margin-inline: auto;
  padding-inline: 20px;
}

/* LEFT: image block */
.about-feature__image {
  position: relative;
}

.about-feature__image img {
  width: 100%;
  height: auto;
  border-radius: 4px;
}

/* floating role tags */
/* ===========================
   FLOATING ROLE TAGS
=========================== */

.tag {
  position: absolute;
  font-size: 0.875rem;
  font-weight: 600;
  color: #fff;
  padding: 6px 12px;
  border-radius: 4px;
  letter-spacing: 0.2px;
  opacity: 0;
  z-index: 2; /* makes sure tags appear above the image */
  animation: fadeInTag 0.8s ease forwards, floatTag 4s ease-in-out infinite;
}

/* Purple tag on top-left */
.tag--purple {
  background: #6b3eff;
  top: 22%;
  left: 6%;
  border-radius: 0;

  animation-delay: 0.3s;
  animation-duration: 4.5s;
}

/* Brown tag on top-right */
.tag--brown {
  background: #a43c25;
  top: 25%;
  right: 30%;
  border-radius: 0;
  animation-delay: 0.6s;
  animation-duration: 4.2s;
}

/* Tag fade and float motion */
@keyframes fadeInTag {
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes floatTag {
  0%, 100% {
    transform: translateY(0);
  }
  50% {
    transform: translateY(-6px);
  }
}

/* RIGHT: text block */
.section-label {
  display: inline-block;
  font-size: 0.85rem;
  color: #6b7280;
  margin-bottom: 12px;
  border: 1px solid #e5e7eb;
  border-radius: 100px;
  padding: 4px 14px;
  background: #fff;
}

.about-feature__text h2 {
  font-weight: 700;
  font-size: clamp(2rem, 1.3rem + 1.6vw, 2.5rem);
  color: #0f172a;
  margin-bottom: 1rem;
}

.about-feature__text .accent {
  color: #ff6a4a;
}

.about-feature__text p {
  color: #4b5563;
  line-height: 1.7;
  margin-bottom: 2rem;
}

.about-feature__text h3 {
  font-weight: 700;
  font-size: 1.25rem;
  margin-bottom: 1rem;
}

/* Feature list */
.feature-list {
  list-style: none;
  padding: 0;
  margin: 0;
  display: grid;
  gap: 1rem;
}

.feature-list li {
  display: flex;
  align-items: flex-start;
  gap: 10px;
  color: #4b5563;
  line-height: 1.6;
}

.feature-list li img {
  width: 20px;
  height: 20px;
  flex-shrink: 0;
  margin-top: 4px;
}
.about-feature__grid{
  grid-template-columns: 1.3fr 1fr;   /* was 1.1fr 1fr */
  align-items: end;                    /* anchors content to section bottom */
}

/* 2) Let the image container act like a stage and anchor image to bottom */
.about-feature__image{
  position: relative;
  display: flex;
  align-items: flex-end;               /* image sits on the bottom */
  justify-content: flex-start;
  min-height: clamp(360px, 42vw, 560px); /* ensures a nice tall stage */
  overflow: visible;
}

/* 3) Enlarge the image and nudge it down to kiss the bottom edge */
.about-feature__image img{
  width: min(720px, 150%);             /* allow >100% of its column */
  max-width: none;
  height: auto;
  display: block;
  object-fit: contain;
  transform: scale(1.2);    /* tiny push so it “seats” on bottom */
  border-radius: 4px;
  margin-bottom: -120px;   /* nudge it down */;
}

/* 4) Slightly reduce section bottom padding so the seating looks intentional */
.about-feature{
  padding-block: clamp(72px, 10vw, 120px) 56px;  /* top  stays fluid, bottom tighter */
}
/* ===== About – Collab (text left, art right) ===== */
:root{
  --collab-pink: #F8EDF6;  /* tweak to match your Figma */
  --split-at: 52%;
}

.about-collab{
  position: relative;
  color: #0f172a;
  background: linear-gradient(
    to right,
    #ffffff 0%,
    #ffffff var(--split-at),
    var(--collab-pink) var(--split-at),
    var(--collab-pink) 100%
  );
  padding-block: clamp(72px, 10vw, 120px);
}

.collab__grid{
  display: grid;
  grid-template-columns: 1.1fr 1fr; /* text | art */
  align-items: center;
  gap: clamp(32px, 5vw, 72px);
  max-width: 1200px;
  margin-inline: auto;
  padding-inline: 20px;
}

.collab__text h2{
  margin: 0 0 1rem;
  font-weight: 700;
  font-size: clamp(2rem, 1.3rem + 1.6vw, 2.5rem);
}
.collab__text .accent{ color:#ff6a4a; }
.collab__text p{ color:#4b5563; line-height:1.7; margin:0 0 2rem; }
.collab__text h3{ font-weight:700; font-size:1.25rem; margin:0 0 1rem; }

/* Icons cluster on the right */
.collab__art{
  display: grid;
  place-items: center;
  padding-block: 24px;
}
.icon-grid{
  display: grid;
  grid-template-columns: repeat(4, minmax(44px, 64px));
  gap: 14px 18px;
}
.icon-grid img{
  width: 100%;
  height: auto;
  display: block;
}

/* ---- Shared feature list from earlier block ---- */
.feature-list{
  list-style:none; margin:0; padding:0; display:grid; gap:1rem;
}
.feature-list li{
  display:flex; align-items:flex-start; gap:10px; color:#4b5563; line-height:1.6;
}
.feature-list li img{ width:20px; height:20px; flex-shrink:0; margin-top:4px; }

/* ===== Responsive ===== */

/* Tablet: stack, keep left alignment */
@media (max-width: 900px){
  .about-collab{
    background: #fff;     /* drop split background for simplicity */
    padding-block: 56px;
  }
  .collab__grid{
    grid-template-columns: 1fr;
    gap: 28px;
    justify-items: start;  /* left align on mobile as requested */
    text-align: left;
  }
  .collab__art{
    order: -1;            /* art above text (optional) */
    width: 100%;
  }

/* Small phones */
@media (max-width: 520px){
  .icon-grid{
    grid-template-columns: repeat(3, 56px); /* fewer columns, bigger icons */
    gap: 12px 14px;
  }
  .collab__text h2{ font-size: 1.72rem; }
  .collab__text p{ font-size: .98rem; }
  .feature-list li img{ width:18px; height:18px; }
}

